__int64 __fastcall rotateFaceClockwise(int a1)		//a1可能值为0、1、2，因为FRU操作主要
{
  __int64 result; // rax
  _DWORD v2[12]; // [rsp+0h] [rbp-40h]
  int m; // [rsp+30h] [rbp-10h]
  int k; // [rsp+34h] [rbp-Ch]
  int j; // [rsp+38h] [rbp-8h]
  int i; // [rsp+3Ch] [rbp-4h]

  for ( i = 0; i <= 2; ++i )
  {
    for ( j = 0; j <= 2; ++j )
    {
      result = j + 3LL * i;
      v2[result] = cube[9 * a1 + 3 * i + j];
    }
  }
  for ( k = 0; k <= 2; ++k )
  {
    for ( m = 0; m <= 2; ++m )
    {
      result = m + 9LL * a1 + 3LL * k;
      cube[result] = v2[3 * (2 - m) + k];
    }
  }
  return result;
}
