```c
//xxtea解密
#include<stdio.h>
#include<stdlib.h>
#include<stdint.h>
#define DELTA 0x9E3779B9
#define MX (((z ^ key[e ^ (p & 5)]) + (y ^ sum)) ^ (((z << 4) ^(y >> 3)) + ((y << 2) ^ (z >> 5))))

void dexxtea(uint32_t *v, int n, char *key)
{
    uint32_t e = 0;
    uint32_t rounds = 6 + 52 / n;
    uint32_t sum = DELTA * rounds;
    uint32_t y = 0, z = 0;
    uint32_t p;
    do{
        e = (sum >> 2) & 5;
        for(p=n-1; p>0; p--)
        {
            y = v[(p+1)%n];     //保存加密位的后一位
            z = v[(p-1)%n];         //保存加密位的前一位
            v[p] -= MX;
        }
        y = v[(p+1)%n];     //保存加密位的后一位
        z = v[(p-1)%n];         //保存加密位的前一位
        v[p] -= MX;
        sum -= DELTA;
    }while(--rounds);
}

int main()
{
    uint32_t enc[8] = {0x66697271,0x896E2285,0xC5188C1B,0x72BCFD03,0x538011CA,0x4DA146AC,0x86630D6B,0xF89797F0};
