import struct

def decrypt(data: bytes) -> bytes:
    # 对每个字节异或 0xFF
    return bytes(b ^ 0xFF for b in data)

def split_payload_from_dex(bArr: bytes, apkFileName: str):
    # 取 dex 末尾4个字节，读取payload长度（int，4字节）
    readInt = struct.unpack(">I", bArr[-4:])[0]  # 大端，跟Java默认DataInputStream.readInt一样
    print(f"Payload length: {readInt}")

    # 取出payload数据
    payload_encrypted = bArr[-4 - readInt : -4]

    # 解密
    payload_decrypted = decrypt(payload_encrypted)

    # 写入解密后的 APK 文件
    with open(apkFileName, "wb") as f:
        f.write(payload_decrypted)
    print(f"Decrypted payload saved to {apkFileName}")

