i = 0
while i < len(data):
    if data[i] > 0 and data[i] < 25:
        n = 1
        tmp = i
        while i < len(data) and data[i] > 0 and data[i] < 25:
            if n == 1:
                flag[data[i]] = data[tmp-n]
            else:
                flag[data[i]] = data[tmp-n] - data[tmp-n+1]
            i += 1
            n += 1
    else:
        i += 1

for i in range(1,25):
    print(chr(flag[i]), end='')
