#include <stdio.h>  
#include <stdlib.h>
#include <stdint.h>

FILE *fp = NULL;

void KSA(uint8_t *S, uint8_t *K)
{
    uint32_t i;
    int j = 0;
    for( i=0; i<512;i++)
    {
        S[i] = -(char)i;
    }
    i = 0;
    while(i<512)
    {
        j = (K[i%13] + S[i] + j) % 512;
        uint8_t temp = S[i];
        S[i] = S[j];
        S[j] = temp;
        i++;
    }
}

void PRGA(uint8_t *S)
{
    int i = 0, j = 0, v8=0 , v9 = 0 ;
    uint8_t temp, k;
    uint8_t b;
    fp = fopen("F:\\StudyFiles\\CTF\\Review_CTF\\XYCTF2024\\RE\\Findme\\Doraemon1", "rb");
    FILE *stream = fopen("./Doraemon3","wb");
    if (fp == NULL) {
        perror("Error opening file");
        exit(1);
    }
    if(stream == NULL) {
        perror("Error opening file");
        exit(1);
    }
    while(!feof(fp))
    {
        i = (i + 1) % 512;
        j = (S[i] + j) % 512;
        temp = S[i];
        S[i] = S[j];
        S[j] = temp;
        k = S[(uint8_t)((S[i] + S[j]) % 512)];
        b = fgetc(fp);
        fputc(b^k, stream);
        srand(S[v8 %512]);
        v9 = rand() % 4;
        for(int t=0; t<v9; t++)
        {
            rand();
            fgetc(fp);
        }
        v8++;
    }
}

int main()
{
    char *key = "Find_Doraemon";
    uint8_t S[512] = {0};

    KSA(S, (uint8_t *)key);
    PRGA(S);
    return 0;
}
