```c
#include <unicorn/unicorn.h>
#include <stdio.h>
#include <string.h>

#define ADDRESS 0x1000
#define DATA_ADDR 0x2000			//最好定义为变量类型，uint32_t
#define CODE_SIZE 0x1000
#define MAX_LEN 64

// x86 32位代码，实现异或加密：
// esi 指向输入，ecx 长度
// 遍历输入字节，对每字节异或0x55
// 返回时修改内存中内容为加密结果
const uint8_t CODE[] = {
    0x31, 0xC0,             // xor eax, eax
    0x31, 0xD2,             // xor edx, edx
    // loop_start:
    0x8A, 0x06,             // mov al, byte ptr [esi]
    0x80, 0xF0, 0x55,       // xor al, 0x55
    0x88, 0x06,             // mov byte ptr [esi], al
    0x46,                   // inc esi
    0x42,                   // inc edx
    0x49,                   // dec ecx
    0x75, 0xF5,             // jne loop_start
    0xC3                    // ret
};

int main() {
    uc_engine *uc;
    uc_err err;

    char input[MAX_LEN];
    const char encrypted_flag[] = {
        'C' ^ 0x55, 'T' ^ 0x55, 'F' ^ 0x55, '{' ^ 0x55, 's' ^ 0x55, 'e' ^ 0x55,
        'c' ^ 0x55, 'r' ^ 0x55, 'e' ^ 0x55, 't' ^ 0x55, '_' ^ 0x55, 'f' ^ 0x55,
        'l' ^ 0x55, 'a' ^ 0x55, 'g' ^ 0x55, '}' ^ 0x55, '\0'
    };

    printf("Enter flag: ");
    if (!fgets(input, sizeof(input), stdin)) {
        printf("Input error\n");
        return -1;
    }
    size_t len = strlen(input);
    if (len > 0 && input[len - 1] == '\n') {
        input[len - 1] = '\0';
        len--;
    }

    if (len != strlen(encrypted_flag)) {
        printf("Flag verification failed.\n");
        return 0;
    }

    // 初始化 Unicorn
    err = uc_open(UC_ARCH_X86, UC_MODE_32, &uc);
    if (err) {
        printf("Failed to initialize Unicorn engine: %u\n", err);
        return -1;
    }

    // 分配内存，写入代码和数据
    uc_mem_map(uc, ADDRESS, CODE_SIZE, UC_PROT_ALL);
    uc_mem_write(uc, ADDRESS, CODE, sizeof(CODE));

    uc_mem_map(uc, DATA_ADDR, CODE_SIZE, UC_PROT_ALL);
    uc_mem_write(uc, DATA_ADDR, input, len);

    // 设置寄存器
    uc_reg_write(uc, UC_X86_REG_ESI, DATA_ADDR);	//最好传入指针&DATA_ADDR，前提是改为变量类型了
    uc_reg_write(uc, UC_X86_REG_ECX, len);			//最好传入&len

    // 执行代码（加密）
    err = uc_emu_start(uc, ADDRESS, ADDRESS + sizeof(CODE), 0, 0);
    if (err) {
        printf("Failed to emulate code: %u\n", err);
        uc_close(uc);
        return -1;
    }

    // 读取加密后的数据
    char encrypted_input[MAX_LEN] = {0};
    uc_mem_read(uc, DATA_ADDR, encrypted_input, len);

    // 比对
    if (memcmp(encrypted_input, encrypted_flag, len) == 0) {
        printf("Flag verified successfully!\n");
    } else {
        printf("Flag verification failed.\n");
    }

    uc_close(uc);
    return 0;
}

```







